import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './pages/HomePage';
import CreateTask from './pages/CreateTask';
import TaskPage from './pages/TaskPage';
import NotFound from './pages/NotFound';
import LoginPage from './pages/LoginPage';   // Added LoginPage import
import ProfilePage from './pages/ProfilePage';  // Added Profile page route
import { isAuthenticated } from './services/authService';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import './App.css';

const App = () => {
  return (
    <Router>
      <div className="app">
        <Navbar />  {/* Navbar at the top */}
        <main className="main-content">
          <Routes>
            {/* Redirect to login if not authenticated */}
            <Route path="/" element={isAuthenticated() ? <HomePage /> : <Navigate to="/login" />} />

            {/* Login route */}
            <Route path="/login" element={<LoginPage />} />

            {/* Protected routes */}
            <Route
              path="/create"
              element={isAuthenticated() ? <CreateTask /> : <Navigate to="/login" />}
            />
            <Route
              path="/tasks/:id"
              element={isAuthenticated() ? <TaskPage /> : <Navigate to="/login" />}
            />

            {/* Profile route */}
            <Route
              path="/profile"
              element={isAuthenticated() ? <ProfilePage /> : <Navigate to="/login" />}
            />

            {/* Not found route */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </main>
        <Footer />  {/* Footer at the bottom */}
      </div>
    </Router>
  );
};

export default App;
